#!/usr/bin/env python3
# -*- coding: iso-8859-15 -*-

import sys

outfile = open(sys.argv[2], "w", encoding="iso-8859-15")

outfile.write('''#ifndef _SPONSORS_H_
#define _SPONSORS_H_

/*
    Copyright © 2003, The AROS Development Team. All rights reserved.
    ****** This file is automatically generated. DO NOT EDIT! *******
*/

#include <utility/tagitem.h>
#include <zune/aboutwindow.h>

struct TagItem *SPONSORS = TAGLIST
(
    SECTION
    (
        SID_NONE''')

file = open(sys.argv[1], "r", encoding="iso-8859-15")
for line in file:
    outfile.write(',\n        NAME("%s")' % line.strip())

outfile.write('''\n    ),
    TAG_DONE
);

#endif /* _SPONSORS_H_ */
''')

file.close()
outfile.close()
